<?php/** Returns student list as options for a select box*/if(!isset($_GET['v'])||!isset($_POST['group'])){    die;}else{ //Enrollment (class,user)    include('../includes/database.php');    $grouplist='';    $roster=$database->groupRoster($_POST['group'],'');    if($roster!=null){        $grouplist.="<option>Choose one...</option>";    foreach($roster as $student){      $grouplist.="<option value='".$student['id']."'>".$student['lname'].", ".$student['fname']."</option>";    }    }else{        $grouplist="<option>There are no students in that group!</option>";    }   echo $grouplist;   }